import React, {Component} from 'react';
import './style.css';
import SearchBar from '../SearchBar';
import api from '../../utils/api';
import Row from '../Row';
import Employee from '../employee';

class Table extends Component {
  state = {
    result: [],
    search: '',
  };

  componentDidMount() {
    api
      .search()
      .then(res => this.setState(() => ({result: res.data.results})))
      .catch(err => console.log(err));
  }

  handleInputChange = event => {
    const {name, value} = event.target;
    this.setState({
      [name]: value,
    });
  };

  handleSearch(search) {
    return Employee => {
      return Employee.first.name.toLowerCase().includes(search.toLowerCase()) 
    }
  }

  render() {
    const newResult = this.state.result;
    let employeeArr = [];

    employeeArr = newResult
      .filter(this.handleSearch(this.state.search))
      .map((event, index) => (
        <Row
          key={index}
          name={event.name.first + event.name.last}
          email={event.email}
        />
      ));
    return (
      <div className="container">
        <div className="row">
          <div className="col-sm">
            <img src="" alt="" />
          </div>
          <div className="col-sm"></div>
          <div className="col-sm"></div>
          <div className="col-sm"></div>
        </div>
      </div>
    );
  }
}

export default Table;
