import React, { Component } from 'react';
import './style.css';

class SearchBar extends Component{

    handleInputChange = event => {
        const {name, value} = event.target;
        this.setState({
          [name]: value
        })
      }

      handleSearch(search){
        return (Employee.name.first.toLowerCase().includes(search.toLowerCase()) || !search)
      }
      render(){
          return (
        <div className="container">
              <div className="row">
                <div className="col-sm-4">
                <input
                className="form-control"
                type="text"
                placeholder="Search an Employee"
                value={props.search}
                onChange={props.handleSearchChange}
                name="search"
                list="names"
              />
                </div>
                <div className="col-sm-4">
                <input
                className="form-control"
                type="email"
                placeholder="Email"
                value={props.searchEmail}
                onChange={props.handleEmailChange}
                name="email"
              />
                </div>
                <div className="col-sm-4">
                <select
                className="form-control"
                value={props.selectRole}
                onChange={props.handleRoleChange}
                name="roleselect"
                placeholder="Select a Role"
              >
                <option>Manager</option>
                <option>Engineer</option>
                <option>Intern</option>
              </select>
                </div>
              <div className="col-sm">
                  <input className="btn" onClick={props.handleSearch()} type="submit" value='Search'/>
              </div>
              </div>
            </div>
          );

      }
};

export default SearchBar;
