import React, { Component } from 'react';
import './style.css';
import SearchBar from '../SearchBar'
import api from '../../utils/api'
import Employee from '../employee';

class Row extends Component {
  state = {
    result: [],
    search: ""
  }

  componentDidMount(){
    api.search().then(res => this.setState(() =>({result: res.data.results })))
    .catch(err => console.log(err));

  }

  handleInputChange = event => {
    const {name, value} = event.target;
    this.setState({
      [name]: value
    })
  }

  handleSearch(search){
    return (Employee.name.first.toLowerCase().includes(search.toLowerCase()) || !search)
  }

  render(props){

    const newResult = this.state.result
    let employeeArr = []

    employeeArr = newResult.filter(this.handleSearch(this.state.search))
    return (
      <div className="container">
        <div className="row">
          <div className="col-sm">
            <img src={props.img} alt="" />
          </div>
          <div className="col-sm">{props.name}</div>
          <div className="col-sm">{props.roles}</div>
          <div className="col-sm">{props.email}</div>
        </div>
      </div>
    );
  };
  }

export default Row;
